    static arb_size_type n_state_vars = 3;
    static arb_field_info parameters[] = {{ "tau1", "ms", 0.5, -.000000, .000000 },
        { "tau2", "ms", 2, -.000000, .000000 },
        { "e", "mV", 0, -.000000, .000000 } };
    static arb_size_type n_parameters = 3;
    static arb_ion_info* ions = NULL;
