#pragma once

#include <cmath>
#include <arbor/mechanism_abi.h>

extern "C" {
  arb_mechanism_type make_arb_new_default_catalogue_hh() {
    // Tables
    static arb_field_info* globals = NULL;
    static arb_size_type n_globals = 0;
    static arb_field_info state_vars[] = {{ "m", "", NAN, -.000000, .000000 },
        { "h", "", NAN, -.000000, .000000 },
        { "n", "", NAN, -.000000, .000000 },
        { "q10", "", NAN, -.000000, .000000 } };
    static arb_size_type n_state_vars = 4;
    static arb_field_info parameters[] = {{ "gnabar", "S / cm2", 0.12, -.000000, .000000 },
        { "gkbar", "S / cm2", 0.036, -.000000, .000000 },
        { "gl", "S / cm2", 0.0003, -.000000, .000000 },
        { "el", "mV", -54.3, -.000000, .000000 } };
    static arb_size_type n_parameters = 4;
    static arb_ion_info ions[] = {{ "na", false, false, false, true, false, false, 0 },
        { "k", false, false, false, true, false, false, 0 } };
    static arb_size_type n_ions = 2;

    arb_mechanism_type result;
    result.abi_version=ARB_MECH_ABI_VERSION;
    result.fingerprint="<placeholder>";
    result.name="hh";
    result.kind=arb_mechanism_kind_density;
    result.is_linear=false;
    result.has_post_events=false;
    result.globals=globals;
    result.n_globals=n_globals;
    result.ions=ions;
    result.n_ions=n_ions;
    result.state_vars=state_vars;
    result.n_state_vars=n_state_vars;
    result.parameters=parameters;
    result.n_parameters=n_parameters;
    return result;
  }

  arb_mechanism_interface* make_arb_new_default_catalogue_hh_interface_multicore();
  arb_mechanism_interface* make_arb_new_default_catalogue_hh_interface_gpu();
}
